sourcesContent:
import bcrypt from 'bcryptjs'; //import thư viện bcrypt js
import db from '../models/index'; //import database
import { where } from 'sequelize';
const salt = bcrypt.genSaltSync(10); // tạo salt hash password
let createNewUser = async (data) => { //hàm tạo user với tham số data
    return new Promise(async (resolve, reject) => { //dùng Promise đảm bảo luôn trả kết quả, trong xử lý bất đồng bộ
        try {
            let hashPasswordFromBcrypt = await hashUserPassword(data.password);
            await db.User.create({
                email: data.email,
                password: hashPasswordFromBcrypt,
                firstName: data.firstName,
                lastName: data.lastName,
                address: data.address,
                phoneNumber: data.phoneNumber,
                gender: data.gender === '1' ? true : false,
                roleId: data.roleId
            })
            resolve('Ok create a new user successful!');
            // console.log('data from service');
            //console.log(data) //log dữ liệu từ biến data
            //console.log(hashPasswordFromBcrypt);
        } catch (e) {
            reject(e)
        }
    })
}
let hashUserPassword = (password) => {
    return new Promise(async (resolve, reject) => { //dùng Promise đảm bảo luôn trả kết quả, trong xử lý bất đồng bộ
        try {
            let hashPassword = await bcrypt.hashSync(password, salt);
            resolve(hashPassword);
        } catch (e) {
            reject(e);
        }
    })
}

//lấy tất cả findAll CRUD
let getAllUser = () => {
    return new Promise(async (resolve, reject) => { //dùng Promise đảm bảo luôn trả kết quả, trong xử lý bất đồng bộ
        try {
            let users = db.User.findAll({
                raw: true, //hiển dữ liệu gốc
            });
            resolve(users); //trả về kết quả
        } catch (e) {
            reject(e)
        }
    })
}
//lấy findOne CRUD
let getUserInfoById = (userId) => {
    return new Promise(async (resolve, reject) => { //dùng Promise đảm bảo luôn trả kết quả, trong xử lý bất đồng bộ
        try {
            let user = await db.User.findOne({
                where: {id:userId}, //query điều kiện cho tham số
                raw:true
            });
            if(user){
                resolve(user); //trả về kết quả
            }else{
                resolve([]); //trả về kết quả rỗng
            }
        } catch (e) {
            reject(e)
        }
    })
}

//hàm put CRUD
let updateUser = (data) =>{
    return new Promise(async (resolve, reject) => { //dùng Promise đảm bảo luôn trả kết quả, trong xử lý bất đồng bộ
        try {
            let user = await db.User.findOne({
                where: { id: data.id } //query điều kiện cho tham số
            });
            if(user){
                user.firstName = data.firstName;
                user.lastName = data.lastName;
                user.address = data.address;
                await user.save();
                //lấy danh sách user
                let allUsers = await db.User.findAll();
                resolve(allUsers);
            }else{
                resolve(); //trả về kết quả rỗng
            }
        } catch (e) {
            reject(e)
        }
    })
}

//hàm xóa user
let deleteUserById = (userId) => {
    return new Promise(async (resolve, reject) => { //dùng Promise đảm bảo luôn trả kết quả, trong xử lý bất đồng bộ
        try {
            let user = await db.User.findOne({
                where: { id : userId }
            })
            if(user){
                user.destroy();
            }
            resolve(); //trả return
        } catch (e) {
            reject(e);
        }
    })
}

module.exports = { //xuất hàm ra bên ngoài
    createNewUser:createNewUser,
    getAllUser: getAllUser,
    getUserInfoById:getUserInfoById,
    updateUser: updateUser,
    deleteUserById: deleteUserById
}

